var Modals = {
    reportEvent: function(action, description)
    {
      ga('send', 'event', 'Modals', action, description);
    },
    alertNoBelongings: function()
    {
        $('#modalNoBelongings').modal();
        $('#list-group-no-belongings').removeClass('hidden');

        this.reportEvent('open', 'No Belongings');
    },
    showChangePasswordModal: function()
    {
        $('#modalChangePassword').modal('show');

        this.reportEvent('open', 'Change Password');
    },
    showDeleteAccountModal: function()
    {
        $('#modalDeleteAccount').modal('show');

        this.reportEvent('open', 'Delete Account');
    },
    showDeviceModal: function()
    {
        $('#modalDevice').modal('show');

        this.reportEvent('open', 'Device Details');
    },
    showChipoloModal: function()
    {
        $('#modalChipolo').modal('show');

        this.reportEvent('open', 'Chipolo Details');
    },
    showAccountSettings: function()
    {
        $('#modalAccountSettings').modal('show');
        this.activateModalTabs();
        this.reportEvent('open', 'Account Settings');
    },
    showNotificationModal: function(message, timeout, redirect)
    {
        $('#modal-notification-message').html(message);
        $('#modalNotification').modal('show');
        this.reportEvent('open', 'Notification Window');

        if (timeout && redirect) {
            setTimeout(function () {
                window.location.href = redirect;
            }, timeout);
        }
    },
    activateModalTabs: function()
    {
        $('.modal-body .tabs .tab').click(function () {
            var tabId = $(this).attr('data-tab');

            Network.accountSettingsActiveTab = tabId;

            // Change tabs
            $('.modal-body .tabs .tab').each(function () {
                var currentTabId = $(this).attr('data-tab');

                $(this).removeClass('tab-current');

                if (currentTabId == tabId) {
                    $(this).addClass('tab-current');
                }
            });

            // Change content
            $('.chipolo-tab-content').each(function () {
                var id = $(this).attr('id');

                $(this).removeClass('hidden');

                if (id != tabId) {
                    $(this).addClass('hidden');
                }
            });

        });
    },
    hideAccountSettings: function()
    {
        $('#modalAccountSettings').modal('hide');
    },
    showGetFreeChipoloModal: function()
    {
        $('#modalGetFreeChipolo').modal('show');
        this.reportEvent('open', 'Free Chipolo (Saasquatch)');
    }
};

